mapper.select:
select 
t.id,t.sys_id,t.rep_no,t.rep_name,t.category,t.address,t.org_id,t.link_user,t.link_phone,t.lon,t.lat,t.description,t.created
,org.org_name
from s_emer_rep t
left join sys_org org on org.id=t.org_id


		and t.id = #{id} 


		and t.sys_id = #{sys_id} 


		and t.rep_no = #{rep_no} 


		and t.rep_name = #{rep_name} 


		and t.category = #{category} 


		and t.address = #{address} 


		and t.org_id = #{org_id} 


		and t.link_user = #{link_user} 


		and t.link_phone = #{link_phone} 


		and t.lon = #{lon} 


		and t.lat = #{lat} 


		and t.description = #{description} 






		${orderStr_} 


		order by created desc

