mapper.select:
select 
t.id,t.sys_id,t.glid,t.xh,t.zxxm,t.zysx,t.zxr,t.cycle,t.cycle_count,t.repeat_,t.zxsj,t.pid,t.pdid,t.status,t.remark,t.creator,t.created,t.modifier,t.modified,t.ext1,t.ext2,t.ext3,t.row_version,t.row_state,t.row_default,t.row_hist,t.sort_no,t.task_req,t.req_id,t.req_model_id 
from s_mbgl_info t 


		and t.id = #{id} 


		and t.sys_id = #{sys_id} 


		and t.glid = #{glid} 


		and t.xh = #{xh} 


		and t.zxxm = #{zxxm} 


		and t.zysx = #{zysx} 


		and t.zxr = #{zxr} 


		and t.cycle = #{cycle} 


		and t.cycle_count = #{cycle_count} 


		and t.repeat_ = #{repeat_} 


		and t.zxsj = #{zxsj} 


		and t.pid = #{pid} 


		and t.pdid = #{pdid} 


		and t.status = #{status} 


		and t.remark = #{remark} 


		and t.creator = #{creator} 


		and t.created = #{created} 


		and t.modifier = #{modifier} 


		and t.modified = #{modified} 


		and t.ext1 = #{ext1} 


		and t.ext2 = #{ext2} 


		and t.ext3 = #{ext3} 


		and t.row_version = #{row_version} 


		and t.row_state = #{row_state} 


		and t.row_default = #{row_default} 


		and t.row_hist = #{row_hist} 


		and t.sort_no = #{sort_no} 


		and t.task_req = #{task_req} 


		and t.req_id = #{req_id} 


		and t.req_model_id = #{req_model_id} 






		${orderStr_} 


		order by t.xh asc

mapper.delete:
delete from s_mbgl_info 
where id in 

	#{item}
