mapper.select:
select 
t.id,t.sys_id,t.xj_id,t.xh,t.rwx,t.rwdd,t.rwjd,t.rwwd,t.remark,t.sort_no,t.row_state,t.row_default,t.row_hist,t.creator,t.created,t.modifier,t.modified,t.ext1,t.ext2,t.ext3,t.row_version 
,concat(t.rwjd,",",t.rwwd) as "lnglat"
from s_xjmbgl_info t 


		and t.id = #{id} 


		and t.sys_id = #{sys_id} 


		and t.xj_id = #{xj_id} 


		and t.xh = #{xh} 


		and t.rwx = #{rwx} 


		and t.rwdd = #{rwdd} 


		and t.rwjd = #{rwjd} 


		and t.rwwd = #{rwwd} 


		and t.remark = #{remark} 


		and t.sort_no = #{sort_no} 


		and t.row_state = #{row_state} 


		and t.row_default = #{row_default} 


		and t.row_hist = #{row_hist} 


		and t.creator = #{creator} 


		and t.created = #{created} 


		and t.modifier = #{modifier} 


		and t.modified = #{modified} 


		and t.ext1 = #{ext1} 


		and t.ext2 = #{ext2} 


		and t.ext3 = #{ext3} 


		and t.row_version = #{row_version} 






		${orderStr_} 


		order by t.xh asc
