mapper.select:
select 
t.id,t.sys_id,t.xjmc,t.xjmb,t.fxid,t.xjlx,t.xjms,t.pid,t.pdid,t.remark,t.sort_no,t.row_state,t.row_default,t.row_hist,t.creator,t.created,t.modifier,t.modified,t.ext1,t.ext2,t.ext3,t.row_version 
,u.user_alias as "modifier_desc"
,o.org_name as "xjmb_desc"
,j.jddgl_mc as "fxid_desc"
,j.jddgl_fxydj as "jddgl_fxydj"
,count(DISTINCT i.id) as "rwsl"
,count(DISTINCT x.id) as "qdsl"
from s_xjmbgl t
left join sys_user u
on t.modifier = u.user_name
left join s_xjmbgl_info i
on t.id=i.xj_id
left join s_jddgl j
on j.id=t.fxid
left join sys_org o
on t.xjmb=o.id
left join s_xjgl x
on t.id=x.mbid



		and t.id = #{id} 


		and t.sys_id = #{sys_id} 


		and t.xjmc = #{xjmc} 


		and t.xjmb = #{xjmb} 


		and t.fxid = #{fxid} 


		and t.xjlx = #{xjlx} 


		and t.xjms = #{xjms} 


		and t.pid = #{pid} 


		and t.pdid = #{pdid} 


		and t.remark = #{remark} 


		and t.sort_no = #{sort_no} 


		and t.row_state = #{row_state} 


		and t.row_default = #{row_default} 


		and t.row_hist = #{row_hist} 


		and t.creator = #{creator} 


		and t.created = #{created} 


		and t.modifier = #{modifier} 


		and t.modified = #{modified} 


		and t.ext1 = #{ext1} 


		and t.ext2 = #{ext2} 


		and t.ext3 = #{ext3} 


		and t.row_version = #{row_version} 


		and ( t.xjmc like
		'%${keywords}%'
		or u.user_alias like '%${keywords}%'
		or o.org_name like '%${keywords}%'
		or j.jddgl_mc like '%${keywords}%'
		)


group by t.id


		${orderStr_} 


		order by created desc

mapper.delete:
delete from s_xjmbgl 
where id in 

	#{item} 
;
delete from s_xjgl_info
where xj_id in

	#{item}
