mapper.select:
select
t.id,t.status,t.username,t.mobile_phone,t.title,t.category,t.description,t.expected_start,t.expected_end,t.real_start,t.real_end,t.sys_id,t.created
,t.address,aa.user_alias
from s_meeting t
left join sys_user aa on t.username=aa.user_name


        and t.sys_id = #{sys_id}


        and t.id = #{id}


        and t.status = #{status}


        and t.username = #{username}


        and t.mobile_phone = #{mobile_phone}


        and t.title = #{title}


        and t.category = #{category}


        and t.description = #{description}


        and t.expected_start = #{expected_start}


        and t.expected_end = #{expected_end}


        and t.real_start = #{real_start}


        and t.real_end = #{real_end}


        and (t.title like concat('%',#{keywords},'%')
        or t.description like concat('%',#{keywords},'%')
        )




        ${orderStr_}


        order by case status when 'RUNNING' then 1 when 'CREATED' then 2 when 'FINISHED' then 3 end, t.created desc

select
t.id,t.status,t.username,t.mobile_phone,t.title,t.category,t.description,t.expected_start,t.expected_end,t.real_start,t.real_end,t.sys_id,t.created
,t.address,aa.user_alias
from s_meeting t
left join sys_user aa on t.username=aa.user_name


        and t.sys_id = #{sys_id}


        and t.id = #{id}


        and t.id in

            #{meetingId}



        and t.status = #{status}


        and (t.username = #{username}
            or (
                t.id in (select meeting_id from s_meeting_member where username=#{username})
                or t.id in (select meeting_id from s_meeting_sign_log where username=#{username})
            )
        )


        and t.mobile_phone = #{mobile_phone}


        and t.title = #{title}


        and t.category = #{category}


        and t.description = #{description}


        and t.expected_start = #{expected_start}


        and t.expected_end = #{expected_end}


        and t.real_start = #{real_start}


        and (
        DATE_FORMAT(t.real_start,'%Y') = #{year} or DATE_FORMAT(t.expected_start,'%Y') = #{year}
        )

            and (
            DATE_FORMAT(t.real_start,'%m') = #{month} or DATE_FORMAT(t.expected_start,'%Y') = #{month}
            )



        and t.real_end = #{real_end}


        and (t.title like concat('%',#{keywords},'%')
        or t.description like concat('%',#{keywords},'%')
        )




        ${orderStr_}


        order by case status when 'RUNNING' then 1 when 'CREATED' then 2 when 'FINISHED' then 3 end, t.created desc

mapper.delete:
delete from s_meeting
where id in

    #{item}

mapper.select:
select t.status, count(1) total from s_meeting t
where (
    t.username = #{username}
    or (
        t.id in (select meeting_id from s_meeting_member where username=#{username})
        or t.id in (select meeting_id from s_meeting_sign_log where username=#{username})
    )
)

    and (
        DATE_FORMAT(t.real_start,'%Y') = #{year} or DATE_FORMAT(t.expected_start,'%Y') = #{year}
    )

        and (
            DATE_FORMAT(t.real_start,'%m') = #{month} or DATE_FORMAT(t.expected_start,'%Y') = #{month}
        )


group by t.status
