mapper.select:
select
t.id,t.meeting_id,t.username
from s_meeting_member t


        and t.id = #{id}


        and t.meeting_id = #{meeting_id}


        and t.username = #{username}




        ${orderStr_}

select
t.id,t.meeting_id,t.username,t2.sign_date, t2.sign_out_date,1 duty, if(t2.id,1,0) come
,t1.user_name,t1.user_alias
from s_meeting_member t
left join sys_user t1 on t.username=t1.user_name
left join s_meeting_sign_log t2 on t2.meeting_id=t.meeting_id and t2.username=t.username


        and t.id = #{id}


        and t.meeting_id = #{meeting_id}


        and t.username = #{username}




        ${orderStr_}

select t.id,t.meeting_id,t.username, t.sign_date, t.sign_out_date,if(t2.id,1,0) duty, 1 come ,t1.user_name,t1.user_alias from s_meeting_sign_log t left join sys_user t1 on t.username=t1.user_name left join s_meeting_member t2 on t2.meeting_id=t.meeting_id and t2.username=t.username
t2.id is null
and t.id = #{id}
and t.meeting_id = #{meeting_id}
and t.username = #{username}
order by t.sign_date desc

mapper.delete:
delete from s_meeting_member


        and id in

            #{item}



        and meeting_id = #{meeting_id}
