mapper.select:
select

,t.xxk_id as "xxk_id"
,t.xxrw_endtime as "xxrw_endtime"
,t.req_model_id as "req_model_id"
,t.user_name as "user_name"

,t1.user_alias as "creator_alias"
,t2.xxkgl_title as "xxkgl_title"
,t2.xxkgl_sources as "xxkgl_sources"
,t2.xxkgl_notes as "xxkgl_notes"

,t.status as "status"
from s_xxrwgl t
left join sys_user t1 on t1.user_name = t.user_name
left join s_xxkgl t2 on t2.id = t.req_model_id


		and t.id = #{id}


		and t.user_name = #{username}


		and t.id in (${ids})


		and t.sys_id = #{sys_id}


		and t.req_model_id = #{req_model_id}


		and t.row_default = #{row_default}


		and t.row_state = #{row_state}


		and t.row_version = #{row_version}


		and (
		t.remark like '%${keywords}%' 
		)




		${orderStr_}


		order by created desc

mapper.delete:
delete from s_xxrwgl 
where id in

	#{item}
